import React from 'react';
import './App.css';
import { Graph } from "react-d3-graph";

// the graph configuration, you only need to pass down properties
// that you want to override, otherwise default ones will be used
const myConfig = {
		"automaticRearrangeAfterDropNode": false,
		"collapsible": false,
		"directed": false,
		"focusAnimationDuration": 0.75,
		"focusZoom": 1,
		"height": 400,
		"highlightDegree": 100,
		"highlightOpacity": 0.5,
		"linkHighlightBehavior": true,
		"maxZoom": 10,
		"minZoom": 0.1,
		"nodeHighlightBehavior": true,
		"panAndZoom": false,
		"staticGraph": false,
		"staticGraphWithDragAndDrop": false,
		"width": 800,
		"d3": {
				"alphaTarget": 0.3,
				"gravity": -150,
				"linkLength": 3,
				"linkStrength": 2,
				"disableLinkForce": false

		},
		"node": {
				"color": " #3D3C3A",
				"fontColor": "black",
				"fontSize": 8,
				"fontWeight": "normal",
				"highlightColor": "#E41B17",
				"highlightFontSize": 8,
				"highlightFontWeight": "bold",
				"highlightStrokeColor": "black",
				"highlightStrokeWidth": "#27332F",
				"labelProperty": "id",
				"mouseCursor": "pointer",
				"opacity": 1,
				"renderLabel": true,
				"size": 210,
				"strokeColor": "#C6DEFF",
				"strokeWidth": 1.6,
				"svg": "",
				"symbolType": "circle"

		},
		"link": {
				"color": "#d3d3d3",
				"fontColor": "black",
				"fontSize": 8,
				"fontWeight": "normal",
				"highlightColor": "#E41B17",
				"highlightFontSize": 8,
				"highlightFontWeight": "normal",
				"labelProperty": "label",
				"mouseCursor": "pointer",
				"opacity": 0.7,
				"renderLabel": true,
				"semanticStrokeWidth": false,
				"strokeWidth": 1.5,
				"markerHeight": 6,
				"markerWidth": 6

		}
};

function App() {

		// make reducer for the words data and internal state 
		const dataReducer = (state, action) =>{
				switch (action.type){
						case 'SET_NEW_NODE':
								return { 
										...state, 
										isLoading: true,
										isError: false 
								};
						default:
								throw new Error();
				}
		}

		// define dispatcher for the Internal data 
		const [stories, dispatchStories] = React.useReducer( storiesReducer, {data: [], IsLoading: false, isError: false } );

		const responce = fetch("https://wordprobe-wordprobe-v1.p.rapidapi.com/translate", {
				"method": "POST",
				"headers": {
						"x-rapidapi-host": "wordprobe-wordprobe-v1.p.rapidapi.com",
						"x-rapidapi-key": "73e5fff5cfmsh0fda3a86ebce802p1ebe6djsnb0012a807d13",
						"content-type": "application/x-www-form-urlencoded"
				},
				"body": {
						"text": "Hello World"
				}
		}).then(response => {
						console.log(response);
				})
				.catch(err => {
						console.log(err);
				});


		// graph payload (with minimalist structure)
		const data_inital = {
				nodes: [{ id: "Harry" }, { id: "Sally" }, { id: "Alice" }],
				links: [
						{ source: "Harry", target: "Sally" },
						{ source: "Harry", target: "Alice" },
				],
		};

		const onClickNode = function(nodeId) {
		};

		const onRightClickNode = function(event, nodeId) {
				window.alert(`Right clicked node ${nodeId}`);
		};

		const onClickLink = function(source, target) {
				window.alert(`Clicked link between ${source} and ${target}`);
		};

		const onRightClickLink = function(event, source, target) {
				window.alert(`Right clicked link between ${source} and ${target}`);
		};

		return (
				<div className="App">
						<Graph
								id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
								data={data_inital}
								config={myConfig}
								onClickNode={onClickNode}
								onRightClickNode={onRightClickNode}
								onClickLink={onClickLink}
								onRightClickLink={onRightClickLink}
						/>
						<button type="button" onClick={addRandomNode}>
								Add random Node
						</button>
				</div>
		);
}

export default App;
