import { initial_state, stateReducer } from './Components/State';
import CarouselContainer from './Components/Carousel';
import SideBarContainer from './Components/SideBar';
import NavBarContainer from './Components/NavBar';
import GraphContainer from './Components/Graph';
import AlertContainer from './Components/Alert';
import React from 'react';
import './App.css';
import { Nav, Navbar, NavDropdown, Form, FormControl, Button, InputGroup, Col, Row } from 'react-bootstrap';

function App() {
		/* define dispatcher for the Internal data */
		const [state, dispatchState] = React.useReducer( stateReducer, initial_state );
		return (
				<div className="App">
						<NavBarContainer 
								state={state}
								dispatchState={dispatchState} />
						<AlertContainer
								state={state}
								dispatchState={dispatchState} />
						{ state.isEmpty? 
								<CarouselContainer/>
								: <div style={{display: 'inline',}}>
										<SideBarContainer 
												state={state}
												dispatchState={dispatchState} />
										<GraphContainer 
												state={state}
												dispatchState={dispatchState} />
								</div>
						}
						<Form.Group
						>
								<Form.Check
										className="m-auto"
										style={{width: 12}} 
										type="radio"
										label="d3"
										value="d3"
										name="formHorizontalRadios"
										id="d3"
										checked={state.graphType === "d3"}
										onChange={handleRadioGraphChange}/ >
								<Form.Check
										type="radio"
										label="2D"
										value="2D"
										name="formHorizontalRadios"
										id="formHorizontalRadios2"
										checked={state.graphType === "2D"}
										onChange={handleRadioGraphChange} />
								<Form.Check
										type="radio"
										label="3D"
										value="3D"
										name="formHorizontalRadios"
										id="formHorizontalRadios3"
										checked={state.graphType === "3D"}
										onChange={handleRadioGraphChange}
								/>
								<NavDropdown.Divider />
								<Form.Check type="switch" id="custom-switch" label="Deep Links"
										value={state.isDeepLinks} 
										onChange={handleToggleDeepLinks}
								/>
						</Form.Group>

				</div>
		);
}

export default App;
