import React from 'react';
import './App.css';
import { Graph } from "react-d3-graph";
import { myConfig, colors }  from "./myConfig";
import Modal from 'react-modal';
import { ProSidebar, Menu, MenuItem, SubMenu  } from 'react-pro-sidebar';
import 'react-pro-sidebar/dist/css/styles.css';


const customStyles = {
		content : {
				top          : '50%',
				left         : '50%',
				right        : 'auto',
				bottom       : 'auto',
				marginRight  : '-50%',
				transform    : 'translate(-50%, -50%)'
		}
};



const API_ENDPOINT = 'http://127.0.0.1:8000/api/v1/'

// graph payload (with minimalist structure)
const initial_state = {
		nodes: [],
		links: [],
		selected:{},
		isError: false,
		isEmpty: true,
		isLoading: true,
		searchTerm: '',
		DestinationTerm: '',
		showDefModal: false,
};

function App() {
		// make reducer for the words data and internal state 
		const stateReducer = (state, action) =>{
				let node;
				switch (action.type){
						case 'SET_NEW_NODES':
								return { 
										...state, 
										nodes: [ ...state.nodes, ...action.payload ],
								};
						case 'SET_NEW_LINKS':
								return { 
										...state, 
										links: [ ...state.links, ...action.payload ]
								};
						case 'SET_NODE_LINK':
								return { 
										...state, 
										nodes: [ ...state.nodes, action.payload.node ],
										links: [ ...state.links, action.payload.link ]
								};
						case 'SET_STATE':
								return { 
										...state, 
										nodes: [ ...state.nodes, ...action.payload.nodes ],
										links: [ ...state.links, ...action.payload.links ]
								};
						case 'SET_SEARCH_NODE':
								return {
										...state,
										nodes: [ action.payload ],
										links: [],
										isEmpty: false,
								};
						case 'SET_NODE_DONE':
								node = state.nodes.filter( node => node.id === action.payload )[0];
								return {
										...state,
										nodes: [ ...state.nodes, { ...node, color:'blue'}], 
										isEmpty: false,
								};
						case 'ERASE_NODES':
								return {
										...state,
										nodes: [],
										links: [],
								};
						case 'SET_SEARCH_TERM':
								return {
										...state,
										searchTerm: action.payload,				
								};
						case 'SET_DEST_TERM':
								return {
										...state,
										destinationTerm: action.payload,
								};
						case 'SET_NODE_SELECTED':
								node = getNode(action.payload)
								return {
										...state,
										selected: { ...node, color: colors.selectedColor},
								};
						case 'SWITCH_SELCTED_NODE':
								return {
										...state,
										selected: node,
								};
						case 'TOGGEL_MODAL':
								return {
										...state,
										showDefModal: !state.showDefModal,
								};
						case 'SET_SHOW_MODAL':
								return {
										...state,
										showDefModal: !action.payload,
								};

						case 'SET_FETCH_FAILED':
								return {
										...state,
										isError: true,
								};
						default:
								throw new Error();
				}
		}

		/* define dispatcher for the Internal data */
		const [state, dispatchState] = React.useReducer( stateReducer, initial_state );

		const processNode = (node) =>{
				/* process a node from the api into one for the dispatcher
				 * it changes title for id, for instance */
				node['id'] = node.title;
				node['isSelected'] = false;
				return node
		}


		// handle the change by seting the state variable to 
		const handleSearchChange = change => dispatchState({ type: 'SET_SEARCH_TERM', payload: change.target.value });
		// handle the change by seting the state variable to 
		const handleDestinationChange = change => dispatchState({ type: 'SET_DEST_TERM', payload: change.target.value });

		// get node with given node id from state 
		const getNode = (nodeId) => state.nodes.filter( node => node.id === nodeId )[0]

		// attemps to return true id node is not in state
		const isNotInState = (nodeId) => state.nodes.every( node => node.id !== nodeId )

		const requestSynonymNodes = (node) => {
				/* for every node request the adjecent node to it */
				node.synonyms.forEach(synonym => {
						if(isNotInState(synonym['synonym'])){
								fetch(API_ENDPOINT + synonym["synonym"])
										.then(result => result.json())
										.then(result => processNode(result))
										.then(adjNode => {
												dispatchState({
														type: 'SET_NODE_LINK', 
														payload: { 
																node: adjNode,
																link: { source: node.id , target: adjNode.id }
														}
												})
										})
										.catch(() => dispatchState({type:'SET_FETCH_FAILED'}))
						}
				})
		}

		// handle submit search button
		const handleSearchSubmit = () =>  {
				fetch(API_ENDPOINT + state.searchTerm)
						.then(result => result.json())
						.then(result => processNode(result))
						.then(node => { 
								dispatchState({type: 'SET_SEARCH_NODE', payload: node});
								return node;
						})
						.then(node => requestSynonymNodes(node))
						.catch(() => dispatchState({type:'SET_FETCH_FAILED'}));
		}

		// handle path button
		const handlePathSubmit = () => dispatchState({ type: 'RESET_NODE' });

		const closeDefModal = () => {
				dispatchState({type: 'SET_SHOW_MODAL', payload: false})
		}

		const onClickNode = function(nodeId) {
				console.log(`clicked node ${nodeId}`);
				requestSynonymNodes(getNode(nodeId))
				dispatchState({type:'SET_NODE_DONE', payload: nodeId})  
				dispatchState({type:'SET_NODE_SELECTED', payload: nodeId})  
		};

		const onRightClickNode = function(event, nodeId) {
				window.alert(`Right clicked node ${nodeId}`);
				dispatchState({type: "TOGGEL_MODAL"})
		};

		const onClickLink = function(source, target) {
				window.alert(`Clicked link between ${source} and ${target}`);
		};

		const onRightClickLink = function(event, source, target) {
				window.alert(`Right clicked link between ${source} and ${target}`);
		};

		return (
				<div className="App">
						<div>
								<InputWithLabel id="search" type="text" isFocuse value={state.searchTerm} onInputChange={handleSearchChange}>
										<strong>Search:</strong>
								</InputWithLabel>
								<button type="button" disable={!state.searchTerm} onClick={handleSearchSubmit}>
										Search
								</button>
						</div>
						<div >
								<InputWithLabel id="destination" type="text" value={state.destinationTerm} onInputChange={handleDestinationChange}>
										<strong>Destination:</strong>
								</InputWithLabel>
								<button type="button" disable={!state.destinationTerm} onClick={handlePathSubmit}>
										Get path
								</button>
						</div>
						<div style={{display: 'inline',}}>
								<div style={{background:'red', float:'left', height:"100%", position: 'absolute', top: '50px'}}>
										<ProSidebar>
												<Menu iconShape="square">
														<MenuItem>Dashboard</MenuItem>
														<SubMenu title="Components">
																<MenuItem>Component 1</MenuItem>
																<MenuItem>Component 2</MenuItem>
														</SubMenu>
												</Menu>
										</ProSidebar>
								</div>
								<div style={{background:'yellow', width:'100%'}}>
										{ state.isEmpty? 
												<p>Search a word</p> 
												:
												<Graph
														id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
														data={state}
														config={myConfig}
														onClickNode={onClickNode}
														onRightClickNode={onRightClickNode}
														onClickLink={onClickLink}
														onRightClickLink={onRightClickLink}
														backgroundColor={"black"}
												/>
										}
								</div>
						</div>
						<div>
								<Modal
										isOpen={state.showDefModal}
										onAfterOpen={() => console.log("models was opend")}
										onRequestClose={closeDefModal}
										style={customStyles}
										contentLabel="Example Modal"
								>
										<div>I am a modal</div>
								</Modal>
						</div>
				</div>
		);
}



const InputWithLabel = ({ id, type, value, onInputChange, isFocused, children }) => {
		/* A component with the Input and a Label */
		// Define an input ref for passing to input component
		const inputRef = React.useRef()
		// set up a side effect it is updates if the focus changes
		React.useEffect(() => { if(isFocused && inputRef.current) inputRef.current.focused(); }, [isFocused])
		return <>
				<label htmlFor={id}> {children} </label>
				&nbsp;
				<input id={id} ref={inputRef} type={type} value={value} onChange={onInputChange}/>
		</>
}

export default App;
