import React, { useState,  useEffect, useLayoutEffect }  from 'react';
import { Graph } from "react-d3-graph";
import { onClickNode } from '../../node_functions';
import { graphConfig }  from "../../myConfig";
//import d3Switcher from "./d3Switcher";
import ForceGraph2D from 'react-force-graph-2d';
import ForceGraph3D from 'react-force-graph-3d';
import _ from "lodash";
//import ForceGraphVR from 'react-force-graph-vr';
//import ForceGraphAR from 'react-force-graph-ar';

/*
 * =======================================
 *      Graph Componente for React 
 * =======================================
 */

function GraphContainer({state, dispatchState}){

		const { 
				//useState,
				//useEffect, 
				useCallback
		} = React;

		// if graph is d3 and previous graph is not d3, the it is switching to d3
		const isSwitchingTo3d = (state.graphType === 'd3' && state.prevGraphType !== 'd3')
		const changeState = useCallback(() => dispatchState({type:'CHANGE_D3_STATE'}), [dispatchState])
/*
		useLayoutEffect(() => {
				console.log("use layout effect")
				//isSwitchingTo3d && setD3State({...state, links:[] })
				isSwitchingTo3d && changeState()
		}, [isSwitchingTo3d, changeState])
		*/

		const changeLinkIdToString = (state) => {
				let d3State = _.cloneDeep(state);
				return state;
				for(let i = 0; i < state.links.length; i++ ){
						console.log(state.links)
						if(!d3State.links[i].source instanceof String){
							d3State.links[i].source = state.links[i].source.id
							d3State.links[i].target = state.links[i].target.id
						}

				}

				console.log(d3State)
				console.log("changed the state")
				return d3State;
		}

/*
		useEffect(() => {  
				console.log('isSwitching: ' + isSwitchingTo3d);
				//isSwitchingTo3d && setD3State(state)
				if(isSwitchingTo3d){
						state.links.forEach((link) => { 
								//setD3State({...state, links: [ ...state.links, {source: link.source.id, target: link.target.id} ]})
								console.log("settin link:")
								console.log(link)

						})
						console.log("set state")
				}

				return () => {
						console.log("clean up ran")

				}
			}, [isSwitchingTo3d, state])
*/

		const handleClick = useCallback( // handle click of node
				(nodeId) => onClickNode(nodeId, state, dispatchState), 
				[state, dispatchState]);


		const chosenGraph = (type) =>{ 
				switch(type) {
						case 'd3':
								//console.log(state)
								if(isSwitchingTo3d){
										//console.log("checking the state")
										//console.log(state)
										//console.log({...state, links:[]})
										return <Graph 
												id="graph-id" 
												// id is mandatory, 
												// if no id is defined rd3g will throw an error
												data={changeLinkIdToString(state)}
												config={graphConfig}
												onClickNode={handleClick}
										/>
								}else{
										//console.log("checking the state")
										console.log(state)
										//console.log({...state, links:[]})
										return <Graph 
												id="graph-id" 
												// id is mandatory, 
												// if no id is defined rd3g will throw an error
												data={changeLinkIdToString(state)}


												config={graphConfig}
												onClickNode={handleClick}
										/>
								}

						case '2D':
								//console.log(state)
								return <ForceGraph2D
										enableNodeDrag={true}
										onNodeClick={handleClick}
										graphData={{...state}}
								/>;
						case '3D':
								//console.log(state)
								return <ForceGraph3D
										enableNodeDrag={true}
										onNodeClick={handleClick}
										graphData={{...state}}
								/>;
						default:
								//console.log(state)
								return <Graph 
										id="graph-id" 
										// id is mandatory, 
										// if no id is defined rd3g will throw an error
										data={state}
										config={graphConfig}
										onClickNode={handleClick}
								/>
				} 
		}


		return <div style={{backgroundImage: 'url(./grid.png)'}} >
				{chosenGraph(state.graphType)}
		</div>

}

export default GraphContainer;
