import React from 'react';
import './App.css';
import { Graph } from "react-d3-graph";

// the graph configuration, you only need to pass down properties
// that you want to override, otherwise default ones will be used
const myConfig = {
		"automaticRearrangeAfterDropNode": false,
		"collapsible": false,
		"directed": false,
		"focusAnimationDuration": 0.75,
		"focusZoom": 1,
		"height": 400,
		"highlightDegree": 100,
		"highlightOpacity": 0.5,
		"linkHighlightBehavior": true,
		"maxZoom": 10,
		"minZoom": 0.1,
		"nodeHighlightBehavior": true,
		"panAndZoom": false,
		"staticGraph": false,
		"staticGraphWithDragAndDrop": false,
		"width": 800,
		"d3": {
				"alphaTarget": 0.3,
				"gravity": -150,
				"linkLength": 3,
				"linkStrength": 2,
				"disableLinkForce": false

		},
		"node": {
				"color": " #3D3C3A",
				"fontColor": "black",
				"fontSize": 8,
				"fontWeight": "normal",
				"highlightColor": "#E41B17",
				"highlightFontSize": 8,
				"highlightFontWeight": "bold",
				"highlightStrokeColor": "black",
				"highlightStrokeWidth": "#27332F",
				"labelProperty": "id",
				"mouseCursor": "pointer",
				"opacity": 1,
				"renderLabel": true,
				"size": 210,
				"strokeColor": "#C6DEFF",
				"strokeWidth": 1.6,
				"svg": "",
				"symbolType": "circle"

		},
		"link": {
				"color": "#d3d3d3",
				"fontColor": "black",
				"fontSize": 8,
				"fontWeight": "normal",
				"highlightColor": "#E41B17",
				"highlightFontSize": 8,
				"highlightFontWeight": "normal",
				"labelProperty": "label",
				"mouseCursor": "pointer",
				"opacity": 0.7,
				"renderLabel": true,
				"semanticStrokeWidth": false,
				"strokeWidth": 1.5,
				"markerHeight": 6,
				"markerWidth": 6

		}
};

function App() {

		// graph payload (with minimalist structure)
		const initial_data = {
				nodes: [{ id: "Harry" }, { id: "Sally" }, { id: "Alice" }],
				links: [
						{ source: "Harry", target: "Sally" },
						{ source: "Harry", target: "Alice" },
				],
		};

		// make reducer for the words data and internal state 
		const dataReducer = (state, action) =>{
				switch (action.type){
						case 'SET_NEW_NODE':
								return { 
										...state, 
										nodes: [ ...state.nodes, action.payload.node],
										links: [ ...state.links, ...action.payload.links ]
								};
						default:
								throw new Error();
				}
		}

		// define dispatcher for the Internal data 
		const [data, dispatchData] = React.useReducer( dataReducer, initial_data );

		// function for getting random num
		function getRandomInt(max) {
				  return Math.floor(Math.random() * Math.floor(max));
		}

		// fuction for getting a random string
		function getRandomStr(length) {
				var result           = '';
				var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
				var charactersLength = characters.length;
				for ( var i = 0; i < length; i++ ) result += characters.charAt(Math.floor(Math.random() * charactersLength));
				   return result;
		}

		const  getRandomLinks = (link_number, word) => {
			var links = [];
				for (var i = 0; i < link_number-1; i++ ){
						links.push({ source: word, target: data.links[getRandomInt(data.links.length-1)]});
				}
			return links;
		}


		// add a random node
		const addRandomNode = () => { 
				const title = getRandomStr(6);
				const links = getRandomLinks(getRandomInt(6), title);
				dispatchData({ 
						type: 'SET_NEW_NODE', 
						payload: { 
								node:{ id: title },
								links: { source: title, target: links}
						}
				});
		}

		const onClickNode = function(nodeId) {
		};

		const onRightClickNode = function(event, nodeId) {
				window.alert(`Right clicked node ${nodeId}`);
		};

		const onClickLink = function(source, target) {
				window.alert(`Clicked link between ${source} and ${target}`);
		};

		const onRightClickLink = function(event, source, target) {
				window.alert(`Right clicked link between ${source} and ${target}`);
		};

		return (
				<div className="App">
						<Graph
								id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
								data={data}
								config={myConfig}
								onClickNode={onClickNode}
								onRightClickNode={onRightClickNode}
								onClickLink={onClickLink}
								onRightClickLink={onRightClickLink}
						/>
						<button type="button" onClick={addRandomNode}>
								Add random Node
						</button>
				</div>
		);
}

export default App;
