import React from 'react';
import './App.css';
import { Graph } from "react-d3-graph";
import { genRandomNode, genGrapNodes, genPath } from "./RandomGenerator";
import  myConfig  from "./myConfig";

function App() {

		// graph payload (with minimalist structure)
		const initial_data = {
				nodes: [{ id: "Harry", fill:'green', type:'Square', color:'red', cx:22, cy:22}, { id: "Sally" }, { id: "Alice" }],
				links: [
						{ source: "Harry", target: "Sally", color:'red' },
						{ source: "Harry", target: "Alice" },
				],
				isError: false,
				isLoading: true,
				searchTerm: '',
				DestinationTerm: '',
		};

		// make reducer for the words data and internal state 
		const dataReducer = (state, action) =>{
				switch (action.type){
						case 'SET_NEW_NODE':
								console.log(action)
								return { 
										...state, 
										nodes: [ ...state.nodes, action.payload.node],
										links: [ ...state.links, ...action.payload.links ]
								};
						case 'APPEND_NODES':
								return {
										...state,
										nodes: [ ...state.nodes, ...action.payload.nodes ],
										links: [ ...state.links, ...action.payload.links ]
								}
						case 'RESET_NODE':
								return {
										...state,
										nodes: [  action.payload.node ],
										links: [],
								};
						case 'SET_FETCH_FAILED':
								return {
										...state,
										isError: true,
								};
						default:
								throw new Error();
				}
		}

		// define dispatcher for the Internal data 
		const [data, dispatchData] = React.useReducer( dataReducer, initial_data );

		// add Async word
		const getWordAsync = (ms=0)=> new Promise((resolve, rejec) => setTimeout(() => resolve(genRandomNode(data.nodes)), ms));

		// get nodes Async
		const getNodesAsync = (word)=> new Promise((resolve, reject)=> setTimeout(()=> resolve(genGrapNodes(word, 0)), 200));

		// get nodes Async
		const getPathAsync =(word, dest)=> new Promise((resolve, reject)=> setTimeout(()=> resolve(genPath(word, dest)), 200));

		// add a random nodes
		const addNodesAsync = () => { 
				getWordAsync()
						.then( (result) => setTimeout(() =>  dispatchData({ type: 'SET_NEW_NODE', payload: result }), 200))
						.catch( () => dispatchData({ type: 'SET_FETCH_FAILED' }) );
		}

		// append array of nodes with a waiting tim in between
		const  dispatchNodesAsync = (result) => {
				if(Array.isArray(result)){
						for(let i in result) setTimeout( () => dispatchData({ type: 'SET_NEW_NODE', payload: result[i] }), 200**i);
				}else{
						dispatchData({ type: 'SET_NEW_NODE', payload: result });
				}
		}

		// Append nodes into the graph
		const appendNodesAsync = (word) => { 
				getNodesAsync(word)
						.then( result => dispatchNodesAsync(result))
						.catch( () => dispatchData({ type: 'SET_FETCH_FAILED' }) );
		}

		// append nodes in a path 
		async function appendPathAsync(word, dest){ 
				getPathAsync(word, dest)
						.then( result => {
								console.log(result);
								dispatchNodesAsync(result);
						})
						.catch( () => dispatchData({ type: 'SET_FETCH_FAILED' }) );
		}

		// fuction for make a persitant state
		const useSemiPersistentSate = key => { 
				const [value, setValue] = React.useState( localStorage.getItem(key) || '');
				React.useEffect( (key) => { localStorage.setItem(key, value)} , [value] );
				return [value, setValue];
		}

		// get state fuctions
		const [ searchTerm, setSearchTerm] = useSemiPersistentSate('search');
		// get state fuctions
		const [ destinationTerm, setDestinationTerm] = useSemiPersistentSate('destination');

		// handle the change by seting the state variable to 
		const handleSearchChange = change => setSearchTerm(change.target.value);
		// handle the change by seting the state variable to 
		const handleDestinationChange = change => setDestinationTerm(change.target.value);

		// handle submit search button
		const handleSearchSubmit = () => { 
				dispatchData({ type: 'RESET_NODE', payload:{ node:{ id: searchTerm } } });
				appendNodesAsync(searchTerm);
		};

		// handle path button
		const handlePathSubmit = () => { 
				dispatchData({ type: 'RESET_NODE', payload:{ node:{ id: searchTerm } } });
				appendPathAsync(searchTerm, destinationTerm);
		};

		const onClickNode = function(nodeId) {
				console.log(`clicked node ${nodeId}`);
				appendNodesAsync(nodeId);
		};

		const onRightClickNode = function(event, nodeId) {
				window.alert(`Right clicked node ${nodeId}`);
		};

		const onClickLink = function(source, target) {
				window.alert(`Clicked link between ${source} and ${target}`);
		};

		const onRightClickLink = function(event, source, target) {
				window.alert(`Right clicked link between ${source} and ${target}`);
		};

		return (
				<div className="App">
						<div>
								<InputWithLabel id="search" type="text" isFocuse value={searchTerm} onInputChange={handleSearchChange}>
										<strong>Search:</strong>
								</InputWithLabel>
								<button type="button" disable={!searchTerm} onClick={handleSearchSubmit}>
										Search
								</button>
								<button type="button" onClick={addNodesAsync}>
										Add random Nodes
								</button>
						</div>
						<div>
								<InputWithLabel id="destination" type="text" value={destinationTerm} onInputChange={handleDestinationChange}>
										<strong>Destination:</strong>
								</InputWithLabel>
								<button type="button" disable={!destinationTerm} onClick={handlePathSubmit}>
										Get path
								</button>
						</div>
						<Graph
								id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
								data={data}
								config={myConfig}
								onClickNode={onClickNode}
								onRightClickNode={onRightClickNode}
								onClickLink={onClickLink}
								onRightClickLink={onRightClickLink}
						/>
				</div>
		);
}

const InputWithLabel = ({ id, type, value, onInputChange, isFocused, children }) => {
		/* A component with the Input and a Label */
		// Define an input ref for passing to input component
		const inputRef = React.useRef()
		// set up a side effect it is updates if the focus changes
		React.useEffect(() => { if(isFocused && inputRef.current) inputRef.current.focused(); }, [isFocused])
		return <>
						<label htmlFor={id}> {children} </label>
						&nbsp;
						<input id={id} ref={inputRef} type={type} value={value} onChange={onInputChange}/>
					</>
}

export default App;
