import React from 'react';
import './App.css';
import { Graph } from "react-d3-graph";
import { genGrapDepth  } from "./RandomGenerator";


function App() {

		// graph payload (with minimalist structure)
		const initial_data = {
				nodes: [{ id: "Harry" }, { id: "Sally" }, { id: "Alice" }],
				links: [
						{ source: "Harry", target: "Sally" },
						{ source: "Harry", target: "Alice" },
				],
		};

		// make reducer for the words data and internal state 
		const dataReducer = (state, action) =>{
				switch (action.type){
						case 'SET_NEW_NODE':
								return { 
										...state, 
										nodes: [ ...state.nodes, action.payload.node],
										links: [ ...state.links, ...action.payload.links ]
								};
						case 'APPEND_NODES':
								return {
										nodes: [ ...state.nodes, ...action.payload.nodes ],
										links: [ ...state.links, ...action.payload.links ]
								}
						case 'SET_FETCH_FAILED':
								return {
										...state,
										isError: true,
								};
						default:
								throw new Error();
				}
		}

		// define dispatcher for the Internal data 
		const [data, dispatchData] = React.useReducer( dataReducer, initial_data );

		// function for getting random num
		function getRandomInt(max) {
				  return Math.floor(Math.random() * Math.floor(max))+1 ;
		}

		// fuction for getting a random string
		function getRandomStr(length) {
				var result           = '';
				var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
				var charactersLength = characters.length;
				for ( var i = 0; i < length; i++ ) result += characters.charAt(Math.floor(Math.random() * charactersLength));
				   return result;
		}

		//generate random link
		const getRandomLinks = (link_number, word) => {
				var seen_targets = [] 
				var links = [];
				for (var i = 0; i < link_number; i++ ){
						var target = data.nodes[getRandomInt(data.nodes.length-1)].id;
						if(!seen_targets.includes(target)){
								links.push({ source: word, target: target });
								seen_targets.push(target);
						}
				}
				return links;
		}

		// Make a random node 
		const genRandomNode = () => {
			const title = getRandomStr(6);
			const links = getRandomLinks(getRandomInt(6), title);
			return { node:{ id: title }, links };
		}


		// add Async word
		const getWordAsync = ()=> new Promise((resolve, reject) => setTimeout(() => resolve(genRandomNode()), 200));

		// add a random node
		const addNodeAsync = () => { 
				getWordAsync()
						.then( result => dispatchData({ type: 'SET_NEW_NODE', payload: result }) )
						.catch( () => dispatchData({ type: 'SET_FETCH_FAILED' }) );
		}

		// get nodes Async
		const getNodesAsync = (word)=> new Promise((resolve, reject)=> setTimeout(()=> resolve(genGrapDepth(word, 2)), 200));

		// append nodes into the graph
		const appendNodesAsync = (word) => { 
				console.log(word);
				getNodesAsync(word)
						.then( result => {
								console.log(result);
								dispatchData({ type: 'APPEND_NODES', payload: result })} )
						.catch( () => dispatchData({ type: 'SET_FETCH_FAILED' }) );
		}

		const onClickNode = function(nodeId) {
				console.log(`clicked node ${nodeId}`);
				appendNodesAsync(nodeId);
		};

		const onRightClickNode = function(event, nodeId) {
				window.alert(`Right clicked node ${nodeId}`);
		};

		const onClickLink = function(source, target) {
				window.alert(`Clicked link between ${source} and ${target}`);
		};

		const onRightClickLink = function(event, source, target) {
				window.alert(`Right clicked link between ${source} and ${target}`);
		};

		return (
				<div className="App">
						<Graph
								id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
								data={data}
								config={myConfig}
								onClickNode={onClickNode}
								onRightClickNode={onRightClickNode}
								onClickLink={onClickLink}
								onRightClickLink={onRightClickLink}
						/>
						<button type="button" onClick={addNodeAsync}>
								Add random Node
						</button>
				</div>
		);
}

export default App;
