import React, { useState, useEffect } from 'react';
import { API_ENDPOINT }  from "../myConfig";
//import { queryNewWord, queryPath } from '../node_functions';
//import { colors }  from "../myConfig";
//import { Nav, Navbar, NavDropdown, Form, FormControl, Button, InputGroup } from 'react-bootstrap';
//import 'bootstrap/dist/css/bootstrap.min.css';
//import { FontAwesomeIcon  } from '@fortawesome/react-fontawesome'
//import { faSlidersH } from '@fortawesome/free-solid-svg-icons'

/*
 * =======================================
 *      Search suggestion componenet
 * =======================================
 * for predicting  what the user is going to type
 */

let query_search = 'querysearch/'

function SuggestionsContainer(props){
		let state = props.state
		let dispatchState = props.dispatchState;
	/* tahek a child fro a input an add suggestions to it */
		// handle the change by seting the state variable to 
		const  [suggestions, setSuggestions] = useState([])	

		useEffect( () => {
				console.log("I ran");
				fetchSuggestions(state.searchTerm);
		}, [state.searchTerm])

		const fetchSuggestions = async (inputText) => 
				/* query server for search suggestionsa
				 * and sets the  */
				fetch(API_ENDPOINT + query_search + inputText)
						.then(result => result.json()) //unpack suggestions
						.then(suggestions => setSuggestions(suggestions))
						.catch(() => dispatchState({type:'SET_ERROR'}));

		const SuggestionList = () =>
				<ul class="suggestions">
						{suggestions.map(
								(suggestion, index) => <li key={index}>suggestion</li> )}
				</ul>

		return <>
				{props.children}
				{state.searchTerm.length > 2? <SuggestionList/>: <></> }
		</>
}

export default SuggestionsContainer;
